// src/Components/AboutPage.jsx
import React from "react";
import { useNavigate } from "react-router-dom";

// Re-using colors for consistency
const C = {
  dark: "#2f2f2f",
  white: "#ffffff",
};

const styles = {
  page: {
    fontFamily:
      'system-ui, -apple-system, "Segoe UI", Roboto, Inter, "Helvetica Neue", Arial, sans-serif',
    padding: "24px 48px",
    flex: 1,
  },
  title: {
    fontSize: "2rem",
    marginBottom: "16px",
  },
  text: {
    fontSize: "1rem",
    lineHeight: "1.5",
    maxWidth: "800px",
  },
};

export default function AboutPage() {
  const navigate = useNavigate();

  return (
    <div style={{ display: "flex", flexDirection: "column", minHeight: "100vh" }}>
      <main style={styles.page}>
        <h1 style={styles.title}>About PriceWise</h1>
        <p style={styles.text}>
          PriceWise is a price comparison tool designed to help you find the best deals on your favorite products. Our mission is to make shopping smarter and more affordable for everyone.
        </p>
        <h2 style={{ ...styles.title, marginTop: "32px" }}>Privacy Policy</h2>
        <p style={styles.text}>
          We are committed to protecting your privacy. We do not store any personal information or search history. Your searches are anonymous and your data is your own.
        </p>
      </main>
    </div>
  );
}
