import React from "react";
import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";
import SearchPage from "./Components/SearchPage.jsx";
import ResultsPage from "./Components/ResultsPage.jsx";
import AboutPage from "./Components/AboutPage.jsx";

export default function App() {
  // Figma-style colors and tokens
  const color = {
    dark: "#2f2f2f",
    white: "#ffffff",
    light: "rgba(255,255,255,0.2)",
  };

  const bar = {
    background: color.dark,
    color: color.white,
    padding: "12px 24px",
    display: "flex",
    alignItems: "center",
    justifyContent: "space-between",
  };

  const brandWrap = { display: "flex", alignItems: "center", gap: 10 };
  const logo = {
    width: 36,
    height: 36,
    borderRadius: 999,
    background: "#555",
    color: "#fff",
    fontSize: 12,
    display: "grid",
    placeItems: "center",
  };
  const brandPill = {
    background: "rgba(255,255,255,0.15)",
    color: "#fff",
    borderRadius: 6,
    padding: "4px 8px",
    fontSize: 14,
  };
  const pillBtn = {
    background: color.light,
    color: "#fff",
    border: 0,
    padding: "6px 10px",
    borderRadius: 6,
    cursor: "pointer",
  };

  return (
    <Router>
      <div
        style={{
          fontFamily:
            'system-ui, -apple-system, "Segoe UI", Roboto, Inter, sans-serif',
          background: color.white,
          minHeight: "100vh",
          display: "flex",
          flexDirection: "column",
        }}
      >
        {/* Top bar styled like Figma */}
        <header style={bar}>
          <Link to="/" style={{ textDecoration: 'none' }}>
            <div style={brandWrap}>
              <div style={logo}>Logo</div>
              <span style={brandPill}>PriceWise</span>
            </div>
          </Link>
          <div style={{ display: "flex", gap: 12 }}>
            <button style={pillBtn}>Favorite</button>
          </div>
        </header>

        {/* Main route content */}
        <div style={{ flex: 1 }}>
          <Routes>
            <Route path="/" element={<SearchPage />} />
            <Route path="/results" element={<ResultsPage />} />
            <Route path="/about" element={<AboutPage />} />
          </Routes>
        </div>

        {/* Footer styled like Figma */}
        <footer style={bar}>
          <Link to="/about">
            <button style={pillBtn}>About</button>
          </Link>
          <Link to="/about">
            <button style={pillBtn}>Privacy</button>
          </Link>
        </footer>
      </div>
    </Router>
  );
}
